mapper.select:
SELECT NVL(COUNT(*), 0) FROM groupboard b join member m ON b.userId = m.userId
groupName =#{groupName}
and

		SELECT * FROM (
			SELECT ROWNUM rnum, tb.* FROM (
				SELECT b.BOARDNUM, b.userId, subject, content, created, nvl(fileCount, 0) fileCount , 
              nvl(g.fileSize,0) fileSize, g.ORIGINALFILENAME
				FROM groupBoard b 
        
				left OUTER join 
		          (
			          select boardnum, nvl(count(*),0) fileCount from GROUPBOARDFILE
			          group by BOARDNUM 
		          ) f on b.BOARDNUM = f.BOARDNUM 
              left outer join 
              GROUPBOARDFILE g on b.BOARDNUM = g.BOARDNUM 
	
			groupName =#{groupName}
		
		and
			
		
	
				ORDER BY boardNum DESC
	                
            ) tb WHERE ROWNUM <= #{end}
        ) WHERE rnum >= #{start}

select boardNum, fileNum, userId, saveFilename, originalFilename, fileSize
 from groupboardFile
  where boardNum =#{boardNum}

SELECT  fileNum, boardNum, saveFilename, 
 originalFilename, fileSize
  FROM groupboardFile WHERE fileNum=#{fileNum}

mapper.update:
UPDATE groupboard SET subject=#{subject},
  content=#{content}
   WHERE boardNum=#{boardNum}

mapper.select:
SELECT * FROM ( SELECT ROWNUM rnum, tb.* FROM ( SELECT gbr.replyNum, boardNum, userId, content, created, gbr.replyAnswer ,NVL(answerCount, 0) answerCount ,NVL(likeCount, 0) likeCount ,NVL(disLikeCount, 0) disLikeCount FROM ( ( SELECT replyNum, r.boardNum, r.userId, r.content, r.created , replyAnswer FROM groupBoardReply r JOIN groupBoard p ON r.boardNum=p.boardNum JOIN member m ON r.userId=m.userId WHERE r.boardNum=#{boardNum} AND replyAnswer=0 ) gbr LEFT OUTER JOIN ( SELECT replyAnswer, COUNT(*) answerCount FROM groupBoardReply GROUP BY replyAnswer
) gbrAnswer ON gbr.replyNum = gbrAnswer.replyAnswer LEFT OUTER JOIN ( SELECT replyNum, COUNT(DECODE(boardReplyLike, 1, 1)) likeCount, COUNT(DECODE(boardReplyLike, 0, 1)) disLikeCount FROM groupBoardReplylike GROUP BY replyNum ) gboardlike ON gbr.replyNum = gboardlike.replyNum ) ORDER BY replyNum DESC
) tb WHERE ROWNUM <= #{end} ) WHERE rnum >= #{start}

   SELECT  NVL(COUNT(DECODE(boardReplyLike, 1, 1)), 0) likeCount,
		        NVL(COUNT(DECODE(boardReplyLike, 0, 1)), 0) disLikeCount
        FROM groupboardReplyLike WHERE replyNum=#{replyNum}
