mapper.select:
SELECT NVL(COUNT(*), 0) FROM groupboard b join member m ON b.userId = m.userId
groupName =#{groupName}
and

		SELECT * FROM (
			SELECT ROWNUM rnum, tb.* FROM (
 				select boardNum, b.userId, m.userName, subject, content,hitCount, created
  					from groupboard b
  					join member m on b.userId = m.userId
	
			groupName =#{groupName}
		
		and
			
		
	
				ORDER BY boardNum DESC
	                
            ) tb WHERE ROWNUM <= #{end}
        ) WHERE rnum >= #{start}

select boardNum, fileNum, userId, saveFilename, originalFilename, fileSize
 from groupboardFile
  where boardNum =#{boardNum}

SELECT  fileNum, boardNum, saveFilename, 
 originalFilename, fileSize
  FROM groupboardFile WHERE fileNum=#{fileNum}

mapper.update:
UPDATE groupboard SET subject=#{subject},
  content=#{content}
   WHERE gbnum=#{gbnum}

mapper.select:
		SELECT * FROM (
		    SELECT ROWNUM rnum, tb.* FROM (
		        SELECT replyNum, boardNum, r.userId, userName 
                      ,content, created
			    FROM groupboardReply r 
	            JOIN member m ON r.userId=m.userId
	            WHERE r.boardNum=#{boardNum}
	            ORDER BY replyNum DESC
	
	        ) tb WHERE ROWNUM <= #{end}
	    ) WHERE rnum >= #{start}
