mapper.select:

     SELECT * FROM (
     SELECT ROWNUM rnum, tb.* FROM (
      SELECT groupName, introduce, userId, created, keyword, place
       FROM ggroup 
               
                        
                           
                        
                     
                ORDER BY groupName DESC
                
                      ) tb WHERE ROWNUM <= #{end}
                  ) WHERE rnum >= #{start}

mapper.update:
UPDATE ggroup SET introduce=#{introduce},
      profile=#{profile}
      WHERE groupName=#{groupName}
