mapper.insert:
INSERT INTO GroupSch (schNum, userId, groupName, subject, color, content, allDay, startDate, endDate, startTime, endTime)
   VALUES (groupsch_seq.NEXTVAL, #{userId}, #{groupName}, #{subject} 
         ,#{color, jdbcType=VARCHAR}
         ,#{content, jdbcType=VARCHAR}
         ,#{allDay, jdbcType=VARCHAR}
         ,#{startDate, jdbcType=VARCHAR}
         ,#{endDate, jdbcType=VARCHAR}
         ,#{startTime, jdbcType=VARCHAR}
         ,#{endTime, jdbcType=VARCHAR}
    )

mapper.select:
SELECT schNum, GROUPNAME, s.userId, userName, subject, color, content, allDay, startDate, endDate, startTime, endTime, created FROM GroupSch s JOIN member m ON s.userId =m.userId
s.GROUPNAME = #{groupName}
AND color=#{group}
AND ((startDate>=#{start} AND startDate<=#{end}) OR (endDate>=#{start} AND endDate<=#{end}))
ORDER BY startDate ASC, schNum DESC

SELECT schNum, s.GROUPNAME, subject, color, content, allDay, startDate, endDate, startTime, endTime, created
    FROM GroupSch s JOIN GROUPmember m ON s.GROUPNAME=m.GROUPNAME
    WHERE schNum=#{schNum}

mapper.update:
UPDATE GroupSch SET 
     title=#{subject}
     ,color=#{color, jdbcType=VARCHAR}
     ,content=#{content, jdbcType=VARCHAR}
     ,allDay=#{allDay, jdbcType=VARCHAR}
     ,startDate=#{startDate, jdbcType=VARCHAR}
     ,endDate=#{endDate, jdbcType=VARCHAR}
     ,startTime=#{startTime, jdbcType=VARCHAR}
     ,endTime=#{endTime, jdbcType=VARCHAR}
     WHERE schNum=#{schNum}
